<?php namespace Application\Model;use Zend\Db\ResultSet\ResultSet;use Zend\Db\TableGateway\TableGateway;use Zend\Db\Sql\Select;use Zend\Paginator\Adapter\DbSelect;use Zend\Paginator\Paginator;class UserTable {    protected $tableGateway;    public function __construct(TableGateway $tableGateway) {        $this->tableGateway = $tableGateway;    }		public function fetch($id)	{		$id  = (int) $id;        $rowset = $this->tableGateway->select(array('user_id' => $id));        $row = $rowset->current();        if (!$row) {            throw new \Exception("Could not find row $id");        }        return $row;	}}